// Select ALL divs and make them have a purple background
$('div').css('background', 'purple');

// Select the DIVS with class "hightlight" and make them 200px wide
$('.highlight').css('width', '200px');

// Select the div with id"third" and give it a orange border
$('#third').css('border', '2px solid orange');

// Select the first div only and change its font color to pink
$('div:first').css('color', 'pink');
